import { Form, Message } from "semantic-ui-react";
import "semantic-ui-css/semantic.min.css";
import styled from "styled-components";
import { useCallback, useState } from "react";

export const Contact: React.FunctionComponent = () => {
  const Root = styled.div`
    padding-top: 8vh;
    width: 100vw;
    height: 90vh;
    display: flex;
    justify-content: center;
    align-items: center;
  `;

  const ContactBox = styled.div`
    width: 50%;
  `;

  const [name, setName] = useState();
  const [subject, setSubject] = useState();
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleName = React.useCallback(() => {
    setName(name);
  }, [name]);

  return (
    <Root>
      <ContactBox>
        <Form
          onSubmit={() => {
            setIsSubmitted(true);
            window.open(
              `mailto:kwokjoey2000@gmail.com?subject=Hello i am ${name}&?body=${subject}asd`
            );
          }}
        >
          <Form.Input
            label="May I know your name?"
            value={name}
            onChange={() => handleName}
          />
          <Form.TextArea
            label="Content"
            placeholder="Any thing that you want to ask me..."
            value={subject}
            onChange={() => setSubject(subject)}
          />
          <Form.Button content="Submit" />
        </Form>
        {isSubmitted && <Message positive>Thank you for your message!</Message>}
      </ContactBox>
    </Root>
  );
};
