import { Form, Message } from "semantic-ui-react";
import "semantic-ui-css/semantic.min.css";
import styled from "styled-components";
import { useState } from "react";

export const Contact: React.FunctionComponent = () => {
  const Root = styled.div`
    padding-top: 8vh;
    width: 100vw;
    height: 90vh;
    display: flex;
    justify-content: center;
    align-items: center;
  `;

  const ContactBox = styled.div`
    width: 50%;
  `;

  const [isSubmitted, setIsSubmitted] = useState(false);

  return (
    <Root>
      <ContactBox>
        <Form>
          <Form.Input label="May I know your name?" />
          <Form.Input label="Whats your email?" />
          <Form.TextArea
            label="Content"
            placeholder="Any thing that you want to ask me..."
          />
          <Form.Button
            content="Submit"
            onClick={() => {
              setIsSubmitted(true);
              window.open("mailto:kwokjoey2000@gmail.com");
            }}
          />
        </Form>
        {isSubmitted && <Message positive>Thank you for your message!</Message>}
      </ContactBox>
    </Root>
  );
};
